INSERT INTO quotes (rnd, text, context) VALUES (21, 'Ko bûs mâlapodam pî vaºa podu? Aizto ja tie kopâ dauzâs, tad viòð saplîst.', './context.jsp?structure=LR&source=Manc1631_Syr&page=539&row=8');
INSERT INTO quotes (rnd, text, context) VALUES (22, 'Vîns un sievas apmânç tâs gudrus.', './context.jsp?structure=LR&source=Manc1631_Syr&page=549&row=27');
INSERT INTO quotes (rnd, text, context) VALUES (23, 'Iedâvenâtas lietas apmânî tos gudrus, un liek tiems iemautu mutç, ka tie nevar sodît.', './context.jsp?structure=LR&source=Manc1631_Syr&page=553&row=9');
INSERT INTO quotes (rnd, text, context) VALUES (24, 'Jeíi tur savu sirdi mutç, bet tie saprotîgi tur savu mut sirdî.', './context.jsp?structure=LR&source=Manc1631_Syr&page=555&row=1');
INSERT INTO quotes (rnd, text, context) VALUES (25, 'Lab tam, kam godîga sieva ir, tas dzîvo otertiek tik ilgi.', './context.jsp?structure=LR&source=Manc1631_Syr&page=563&row=28');
INSERT INTO quotes (rnd, text, context) VALUES (26, 'To jeíu valoda ir par lieku apnicîga, un viòu smieðana ir tikai grçki, un tas vçl ar to kuslojâs.', './context.jsp?structure=LR&source=Manc1631_Syr&page=566&row=8');
INSERT INTO quotes (rnd, text, context) VALUES (27, 'Turi tavu draugu cienîgi, un turi viòam, ko tu soli.', './context.jsp?structure=LR&source=Manc1631_Syr&page=566&row=16');
INSERT INTO quotes (rnd, text, context) VALUES (28, 'Ît ka kad tu putnu no rokas izlaid, tâ ir, kad tu tavu draugu pamet.', './context.jsp?structure=LR&source=Manc1631_Syr&page=566&row=21');
INSERT INTO quotes (rnd, text, context) VALUES (29, 'Nesniedzies pçc visu, ko tu redz.', './context.jsp?structure=LR&source=Manc1631_Syr&page=574&row=1');
INSERT INTO quotes (rnd, text, context) VALUES (30, 'Un kad tâ pasirds mçrîgi turçta top, tad viegli var gulçt. Tad var rîtos agri celties, un ir labi pî sev paðam.', './context.jsp?structure=LR&source=Manc1631_Syr&page=574&row=12');
INSERT INTO quotes (rnd, text, context) VALUES (31, 'Saprotîgs vîrs neniecina labu padomu.', './context.jsp?structure=LR&source=Manc1631_Syr&page=576&row=20');
INSERT INTO quotes (rnd, text, context) VALUES (32, 'Bet traks un lepnis nebîstâs nenieka, darîdams, ko gribçdams.', './context.jsp?structure=LR&source=Manc1631_Syr&page=576&row=21');
INSERT INTO quotes (rnd, text, context) VALUES (33, 'Kam raksts jâmâcâs, tas nevar citu darbu darît, un kad jâmâca, tam nebûs citas lietas darît.', './context.jsp?structure=LR&source=Manc1631_Syr&page=588&row=9');
INSERT INTO quotes (rnd, text, context) VALUES (34, 'Stabules un kokles skan krâsni, bet viena mîlîga valoda labâk nekâ tie abi divi.', './context.jsp?structure=LR&source=Manc1631_Syr&page=593&row=17');
INSERT INTO quotes (rnd, text, context) VALUES (35, 'Tava acs redz labprât, kas mîlîgs un jauks ir, bet zaïu sçklu labâki nekâ tos abi divi.', './context.jsp?structure=LR&source=Manc1631_Syr&page=593&row=19');
INSERT INTO quotes (rnd, text, context) VALUES (36, 'Viens draugs nâk pî otru bçdâs, bet vîrs und sieva daudz vairâk.', './context.jsp?structure=LR&source=Manc1631_Syr&page=593&row=21');
INSERT INTO quotes (rnd, text, context) VALUES (37, 'Viens brâlis palîdz otram bçdâs, bet þçïastîba palîdz daudz vairâk.', './context.jsp?structure=LR&source=Manc1631_Syr&page=593&row=23');
INSERT INTO quotes (rnd, text, context) VALUES (38, 'Zelts un sudrabs uztur vîru, bet visi vairâk labs padoms.', './context.jsp?structure=LR&source=Manc1631_Syr&page=593&row=25');
INSERT INTO quotes (rnd, text, context) VALUES (38, 'Kas bedri dara, tas iekritîs, un kas akmeni veï, uz to tas nâks.', './context.jsp?structure=LR&source=Manc1637_Sal&page=87&row=22');
INSERT INTO quotes (rnd, text, context) VALUES (39, 'Kad malkas vairs neir, tad izdziest uguns, un kad tas zaimotâjs nost ir, tad mitçjâs tâ bârðana.', './context.jsp?structure=LR&source=Manc1637_Sal&page=87&row=4');
INSERT INTO quotes (rnd, text, context) VALUES (40, 'Tu gribi maíenît gulçt un maíenît snaust, un maíenît tâs rokas kopâ likt, ka tu dusçt vari. Bet tava nabadzîba tev uzies kâ kâds ceïavîrs, un tava vajadzîba kâ kâds apbruòots vîrs.', './context.jsp?structure=LR&source=Manc1637_Sal&page=81&row=12');
INSERT INTO quotes (rnd, text, context) VALUES (41, 'Prâtîgs vîrs neuzpûðâs ar savu zinâðanu, bet tâ jeía sirds izkliedz savu trakumu.', './context.jsp?structure=LR&source=Manc1637_Sal&page=39&row=7');
INSERT INTO quotes (rnd, text, context) VALUES (42, 'Ko dara daþs knçvels? Uzaudzis maganît, ka jau var pieguïâ jât un sev bandas sçties, nedod tçvam un mâtei labu vârdu, izgâna tos kâ suni.', './context.jsp?structure=LR&source=Manc1654_LP3&page=175&row=8');
INSERT INTO quotes (rnd, text, context) VALUES (43, 'Redzi, tâ ir vecos laikos veci ïaudis tieðâm runâjuði: viens tçvs var desmits un vairâk bçrnus uzaudzenât, pieçdenât, ìçrbt un kopt. Bet desmits bçrni nevar ne vienu tçvu uzturçt.', './context.jsp?structure=LR&source=Manc1654_LP3&page=175&row=18');
INSERT INTO quotes (rnd, text, context) VALUES (44, 'Ka tu sargâts topi no tâs niknas sievas, no tâs mîkstas mçles tâs sveðas.', './context.jsp?structure=LR&source=Manc1637_Sal&page=19&row=8');
INSERT INTO quotes (rnd, text, context) VALUES (45, 'Dusmîgs vîrs iesâk bârðanu, bet viens pieticîgs klusina to rieðanu.', './context.jsp?structure=LR&source=Manc1637_Sal&page=48&row=9');
INSERT INTO quotes (rnd, text, context) VALUES (46, 'Labâk ir viens çdiens kâpostu ar mîlestîbu nekâ barots vçrsis ar ienîdçðanu.', './context.jsp?structure=LR&source=Manc1637_Sal&page=48&row=6');
INSERT INTO quotes (rnd, text, context) VALUES (47, 'Prieks ir cilvçkam, kad tam pareize top atbildçts, un viens vârds îstenâ laikâ ir ïoti jauks.', './context.jsp?structure=LR&source=Manc1637_Sal&page=48&row=23');
INSERT INTO quotes (rnd, text, context) VALUES (48, 'Bçdas sirdi çd, bet mîlîgs vârds iepriecina.', './context.jsp?structure=LR&source=Manc1637_Sal&page=39&row=12');
INSERT INTO quotes (rnd, text, context) VALUES (49, 'Tam jeíam ir tâ jeíîba prieks, bet viens prâtîgs vîrs paliek uz to îstenu ceïu.', './context.jsp?structure=LR&source=Manc1637_Sal&page=48&row=17');
INSERT INTO quotes (rnd, text, context) VALUES (50, 'Neesi pî tiems, kas savu roku apíîlo un par parâdu galvo.', './context.jsp?structure=LR&source=Manc1637_Sal&page=73&row=16');